package dev.tonholo.awesomeapp.navigationimport androidx.navigation.NavControllerimport androidx.navigation.NavGraphBuilderimport androidx.navigation.NavTypeimport androidx.navigation.compose.composableimport androidx.navigation.navArgumentimport dev.tonholo.awesomeapp.features.feed.FeedScreenimport dev.tonholo.awesomeapp.features.imagedetail.ImageDetailScreenobject Destinations {    object Feed : Destination {        override val route = "feed"        override fun setup(navController: NavController, builder: NavGraphBuilder) = with(builder) {            composable(Feed.route) {                FeedScreen(navController)            }        }    }    object ImageDetails : Destination {        private const val PARAM_ID = "id"        private const val baseRoute = "image_detail"        override val route = "$baseRoute/{$PARAM_ID}"        override fun setup(navController: NavController, builder: NavGraphBuilder) = with(builder) {            composable(                route = ImageDetails.route,                arguments = listOf(                    navArgument(PARAM_ID) {                        type = NavType.StringType                    }                ),            ) {                val id = it.arguments?.getString(PARAM_ID)                    ?: throw IllegalArgumentException("Can't access image detail without id")                ImageDetailScreen(imageId = id, navController = navController)            }        }        operator fun invoke(id: String) = "$baseRoute/$id"    }}