package dev.tonholo.awesomeapp.data.remoteimport retrofit2.http.GETimport retrofit2.http.Query/** * Represents the requests for Unsplash API. * @see <a href="https://unsplash.com/documentation">Unsplash API docs</a> for more reference */interface UnsplashApi {    @GET("/photos")    suspend fun getPhotos(        page: Int = 1,        @Query("per_page") perPage: Int = 10,        @Query("order_by") orderBy: String = ORDER_BY_LATEST,    ): List<UnsplashApi>    companion object {        const val ORDER_BY_LATEST = "latest"        const val ORDER_BY_OLDEST = "oldest"        const val ORDER_BY_POPULAR = "POPULAR"    }}