package dev.tonholo.awesomeapp.features.cameraimport androidx.lifecycle.ViewModelimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.asStateFlowimport javax.inject.Inject@HiltViewModelclass CameraViewModel @Inject constructor() : ViewModel() {    private val _state = MutableStateFlow(CameraScreenState())    val state = _state.asStateFlow()    private fun reduce(reducer: CameraScreenState.() -> CameraScreenState) {        _state.value = reducer(_state.value)    }    fun onFlipCameraClick() {        reduce { copy(isRearCamera = !isRearCamera) }    }}