package dev.tonholo.awesomeapp.features.camera.utilimport android.content.Contextimport androidx.camera.lifecycle.ProcessCameraProviderimport androidx.core.content.ContextCompatimport kotlin.coroutines.resumeimport kotlin.coroutines.suspendCoroutine/** * Gets the [ProcessCameraProvider] from context using coroutine. */suspend fun Context.cameraProvider() = suspendCoroutine<ProcessCameraProvider> { continuation ->    ProcessCameraProvider.getInstance(this).also { cameraProvider ->        cameraProvider.addListener({            continuation.resume(cameraProvider.get())        }, ContextCompat.getMainExecutor(this))    }}