package dev.tonholo.awesomeapp.features.feedimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport dev.tonholo.awesomeapp.di.common.IODispatcherimport dev.tonholo.awesomeapp.features.feed.usecase.LoadImagesUseCaseimport kotlinx.coroutines.CoroutineDispatcherimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.asStateFlowimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass FeedViewModel @Inject constructor(    @IODispatcher ioDispatcher: CoroutineDispatcher,    loadImagesUseCase: LoadImagesUseCase,) : ViewModel() {    private val _state = MutableStateFlow(FeedState())    val state = _state.asStateFlow()    init {        viewModelScope.launch(ioDispatcher) {            loadImagesUseCase().collect { state ->                when (state) {                    is LoadImagesUseCase.Result.Error ->                        reduceState {                            copy(hasError = true, errorMessage = state.message)                        }                    LoadImagesUseCase.Result.Loading -> reduceState {                        copy(isLoading = true)                    }                    is LoadImagesUseCase.Result.Success -> reduceState {                        copy(images = state.images)                    }                }            }        }    }    private fun reduceState(reducer: FeedState.() -> FeedState) {        _state.value = reducer(_state.value)    }}