package dev.tonholo.awesomeapp.ui.common.toolbarimport android.content.res.Configurationimport androidx.compose.material3.SmallTopAppBarimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.tooling.preview.Previewimport dev.tonholo.awesomeapp.ui.theme.AwesomeAppTheme@Composablefun AppToolbar(    title: String,) {    SmallTopAppBar(        title = {            Text(title)        }    )}@Preview(    uiMode = Configuration.UI_MODE_NIGHT_NO,)@Composableprivate fun Preview() {    Preview(false)}@Preview(    uiMode = Configuration.UI_MODE_NIGHT_YES,)@Composableprivate fun DarkPreview() {    Preview(true)}@Composableprivate fun Preview(    darkMode: Boolean,) {    AwesomeAppTheme(darkTheme = darkMode) {        AppToolbar("Preview toolbar")    }}